
public class Ders7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		boolean --> true or false    					1byte
		char    --> single character 'c' 				2bytes
		byte 	--> integer          					1byte
		short   --> integer          					2bytes
		int     --> integer			 					4bytes
		long 	--> integer   		 					8bytes
		float   --> floating-point number(ondalıklı)    4bytes	
		double 	--> floating-point number(ondalıklı)    8bytes	
		 */
		
		// en çok kullanılan veri tipleri double ve int tir.
		double sayi = 12.5; 
		sayi = -129;
		
		char karakter = 'A';
		
		boolean dogruMu = false;
		
		System.out.println(sayi);
		System.out.println(karakter);
		System.out.println(dogruMu);
		
		
	}

}
